        private Random random = new Random(0);

        public Configuration()
        {
            this.AutomaticMigrationsEnabled = true;
            this.AutomaticMigrationDataLossAllowed = false;
        }

        protected override void Seed(ApplicationDbContext context)
        {
            if (context.Users.Any())
            {
                // Seed initial data only if the database is empty
                return;
            }

            var towns = this.SeedTowns(context);
            var users = this.SeedApplicationUsers(context, towns);
            var categories = this.SeedCategories(context);
            this.SeedAdvertisements(context, users, categories, towns);
        }

        private IList<Town> SeedTowns(ApplicationDbContext context)
        {
            var towns = new List<Town>();
            var townNames = new List<string>
            {
                "Sofia", "Plovdiv", "Varna", "Burgas", "Ruse", "Stara Zagora", "Pleven",
                "Sliven", "Dobrich", "Shoumen", "Yambol", "Blagoevgrad", "Veliko Tarnovo"
            };

            foreach (var townName in townNames)
            {
                var town = new Town { Name = townName };
                context.Towns.Add(town);
                towns.Add(town);
            }

            context.SaveChanges();

            return towns;
        }

        private IList<ApplicationUser> SeedApplicationUsers(
            ApplicationDbContext context, IList<Town> towns)
        {
            var usernames = new string[] { "admin", "maria", "peter", "kiro", "didi" };

            var users = new List<ApplicationUser>();
            var userStore = new UserStore<ApplicationUser>(context);
            var userManager = new UserManager<ApplicationUser>(userStore);
            userManager.PasswordValidator = new PasswordValidator
            {
                RequiredLength = 2,
                RequireNonLetterOrDigit = false,
                RequireDigit = false,
                RequireLowercase = false,
                RequireUppercase = false,
            };

            foreach (var username in usernames)
            {
                var name = username[0].ToString().ToUpper() + username.Substring(1);
                var user = new ApplicationUser
                {
                    UserName = username,
                    Name = name,
                    Email = username + "@gmail.com",
                    PhoneNumber = this.GenerateRandomPhoneNumber(),
                    Town = towns[random.Next(towns.Count)]
                };

                var password = username;
                var userCreateResult = userManager.Create(user, password);
                if (userCreateResult.Succeeded)
                {
                    users.Add(user);
                }
                else
                {
                    throw new Exception(string.Join("; ", userCreateResult.Errors));
                }
            }

            // Create "Administrator" role
            var roleManager = new RoleManager<IdentityRole>(new RoleStore<IdentityRole>(context));
            var roleCreateResult = roleManager.Create(new IdentityRole("Administrator"));
            if (! roleCreateResult.Succeeded)
            {
                throw new Exception(string.Join("; ", roleCreateResult.Errors));
            }

            // Add "admin" user to "Administrator" role
            var adminUser = users.First(user => user.UserName == "admin");
            var addAdminRoleResult = userManager.AddToRole(adminUser.Id, "Administrator");
            if (! addAdminRoleResult.Succeeded)
            {
                throw new Exception(string.Join("; ", addAdminRoleResult.Errors));
            }

            // Remove admin's town
            adminUser.Town = null;

            context.SaveChanges();

            return users;
        }

        private string GenerateRandomPhoneNumber()
        {
            return "+359 8" + this.random.Next(80,100) + " " + this.random.Next(100000,1000000);
        }

        private IList<Category> SeedCategories(ApplicationDbContext context)
        {
            var categories = new List<Category>();
            var categoryNames = new List<string>
            {
                "Cars", "Electronics", "Properties", "Jobs", "Education",
                "Business", "Phones", "Sports", "Furniture", "Animals", "Others"
            };

            foreach (var categoryName in categoryNames)
            {
                var category = new Category { Name = categoryName };
                context.Categories.Add(category);
                categories.Add(category);
            }

            context.SaveChanges();

            return categories;
        }

